import React from 'react'
import { IBill } from '../server/src/models/accounts/Enrollment'
import { Paper, Typography, Button } from '@material-ui/core'

export default function Bill({ bill }: { bill: IBill }) {
  var isPaid = bill.payments.reduce((s, p) => s + p.amount, 0) >= bill.amount

  const statusColor = () => {
    if (isPaid) return 'green'
    return 'red'
  }

  return (
    <Paper style={{ padding: 10, marginBottom: 10 }}>
      <div style={{ display: 'flex', paddingBottom: 5, marginBottom: 10 }}>
        <div style={{ flexGrow: 1 }}>
          <Typography variant="body1" color="secondary">{bill.description}</Typography>

          <div style={{ color: '#808080', fontSize: '9pt' }}>
            <strong>Fee type:</strong> {bill.feeType}, &nbsp;
            <strong>Amount:</strong> {bill.amount}, &nbsp;
            <strong>Status:</strong> <span style={{ color: statusColor() }}>{isPaid ? 'Paid' : 'Not Paid'}</span>
          </div>
        </div>

        <div style={{ color: '#808080', fontSize: '9pt' }}>
          <Button size="small" variant="text" color="secondary">Print Bill</Button>
        </div>
      </div>

      <div style={{ color: '#808080', marginBottom: 5 }}><strong>Payments</strong></div>
      {bill.payments.length > 0 ? 
        bill.payments.map(p => (
          <div style={{ display: 'flex', padding: '5px 0px', borderTop: '1px solid #DFDFDF', color: '#808080', fontSize: '9pt' }}>
            <div style={{ flexGrow: 1 }}>
              <strong>Amount:</strong> {p.amount}, &nbsp;
              <strong>Date:</strong> {p.date}
            </div>
          </div>
        )) :
        <div style={{ padding: '10px 0px' }}>(No payment)</div>
      }

      <div style={{ color: '#808080', fontSize: '9pt', paddingTop: 5, marginTop: 10, borderTop: '1px solid #DFDFDF', textAlign: 'right' }}>
        <strong>Total paid:</strong> {bill.payments.map(p => p.amount).reduce((s, p) => s + p, 0)}, &nbsp;
        <strong>Remaining:</strong> <span style={{ color: 'orange' }}>{Math.max(0, bill.amount - (bill.payments.map(p => p.amount).reduce((s, p) => s + p, 0)))}</span>
      </div>
    </Paper>
  )
}
