import * as React from 'react'
import Container from '../components/Container'
import MainLayout from '../layouts/MainLayout'
import { Typography } from '@material-ui/core'
import DepartmentList from '../components/catalog/DepartmentList'
import withApollo from '../lib/with-apollo'
import withAuth from '../lib/with-auth'
import { gql } from 'apollo-boost'
import IDepartment from '../server/src/models/catalog/Department'

const GET_DEPARTMENTS_QUERY = gql`
  query getDepartments {
    departments {
      id
      title
      programs {
        id title description logo
      }
    }
  }
`

function IndexPage({ departments }: { departments: IDepartment[] }) {
  return (
    <MainLayout title="IMIT">
      <img src="/images/banner.jpg" alt="" style={{width: '100%'}} />

      <Container>
        <div style={{margin: '15px 0px', fontSize: '9pt', color: '#808080'}}>
          Start your journey here. Browse our trainings. They are gouped by departments, and each department
          offer a wide variety of programs. Pick up the right program for you to see the available trainings.
          Do not hesitate to <a href="#" style={{ textDecoration: 'none' }}>contact us</a> if you need assistance to choose the program that 
          best fit you.
        </div>

        <DepartmentList departments={departments} />
      </Container>
    </MainLayout>
  )
}

IndexPage.getInitialProps = async ({ apolloClient, token }: any) => {
  const { data: { departments }} = await apolloClient.query({
    query: GET_DEPARTMENTS_QUERY,
    context: { headers: { 'schoolman_token': token } }
  })

  return ({ departments, authRequired: false })
}

export default withApollo(withAuth(IndexPage))
