import * as React from 'react'
import {makeStyles, createStyles, Theme} from '@material-ui/core/styles'
import { Typography, Grid } from '@material-ui/core'
import IDepartment from '../../server/src/models/catalog/Department'
import Link from 'next/link'
import ProgramSummary from './ProgramSummary'
import { textUpTo } from '../../lib/utilities'

type Props = { department: IDepartment }

function compare( a: any, b: any ) {
  if ( a.title < b.title ){
    return -1;
  }
  if ( a.title > b.title ){
    return 1;
  }
  return 0;
}

export default function DepartmentBox({ department }: Props) {
  const classes = makeStyles((theme: Theme) => createStyles({
    root: {
      marginTop: 40
    },
    title: {
      backgroundColor: '#DFDFDF',
      color: '#000000',
      fontSize: '10pt',
      padding: 5,
      borderRadius: 3,
      fontWeight: 'bold',
      marginBottom: 15
    }
  }))()

  const { title, programs } = department

  return (
    <div className={classes.root}>
      <div className={classes.title}><Typography variant="subtitle2">{title}</Typography></div>


        {programs.map(p => 
          <Grid container style={{ borderBottom: '1px solid #DFDFDF', marginBottom: 15 }}>
            <Grid item xs={12} sm={12} md={6}>
              <Link key={p.id} href={{ pathname: '/program', query: { programId: p.id} }} passHref>
                <ProgramSummary program={p} />
              </Link>
            </Grid>

            <Grid item xs={12} sm={12} md={6}>
              <div>
                {p.trainings.sort(compare).map(t => 
                  <div style={{ marginBottom: 15 }}>
                    <Link href={{ pathname: '/training', query: { trainingId: t.id } }}>
                      <a style={{ color: '#339967', textDecoration: 'none' }}>{t.title}</a>
                    </Link>
                    <div style={{ fontSize: '9pt', color: '#808080' }}>{textUpTo(t.description, 180)}</div>
                  </div>
                )}
              </div>
            </Grid>
          </Grid>
        )}
    </div>
  )
}
