import React from 'react'
import {
  AppBar, 
  Toolbar, 
  IconButton, 
  Typography, 
  Button, 
  Menu, 
  MenuItem, 
  Divider, 
  Drawer, 
  ListItem, 
  ListItemText, 
  List, 
  ListSubheader,
  Hidden
} from '@material-ui/core'
import MenuIcon from '@material-ui/icons/Menu'
import AccountCircleIcon from '@material-ui/icons/AccountCircle'
import { makeStyles, Theme, createStyles } from '@material-ui/core/styles'
import Router, { useRouter } from 'next/router'
import IAccountFields from '../server/src/models/accounts/Account'
import { removeCookie } from '../lib/cookie'
import { useAuth } from './AuthContext'
import { useApolloClient } from '@apollo/react-hooks'
import { authCookieName } from '../app.config'

function ProfileMenu({ currentUser, onSignout }: { currentUser: IAccountFields, onSignout: Function }) {
  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null)
  const handleClick = (event: React.MouseEvent<HTMLButtonElement>) => setAnchorEl(event.currentTarget)
  const handleClose = () => setAnchorEl(null)

  return (
    <div>
      <IconButton aria-controls="simple-menu" aria-haspopup="true" color="inherit" onClick={handleClick}>
        <AccountCircleIcon />
      </IconButton>

      <Menu
        id="simple-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
        <Typography style={{ padding: '5px 15px', color: '#808080' }}>{currentUser.fullName}</Typography>
        <Divider />
        <MenuItem onClick={() => { handleClose(); Router.push('/myProfile') }}>My profile</MenuItem>
        <MenuItem
          onClick={() => {
            handleClose()
            Router.push('/resetPassword')
          }}
        >
          Reset my password
        </MenuItem>

        <Divider />

        <MenuItem
          onClick={() => {
            handleClose()
            onSignout()
          }}
        >
          Sign Out
        </MenuItem>
      </Menu>
    </div>
  )
}

function MobileMenu(props: { drawerOpen: boolean, closeDrawer: Function, currentUser?: IAccountFields, onSignout: Function }) {
  const classes = makeStyles({
    list: {
      width: 250,
    },
    fullList: {
      width: 'auto',
    },
  })()

  const router = useRouter()

  return (
    <Drawer anchor="right" open={props.drawerOpen} onClose={props.closeDrawer()}>
      <div
        className={classes.list}
        role="presentation"
        onClick={props.closeDrawer()}
        onKeyDown={props.closeDrawer()}
      >
        <List>
          <ListItem button key="0" onClick={() => router.push('/')}>
            <ListItemText primary="Catalog" />
          </ListItem>

          {props.currentUser &&
            <ListItem button key="0" onClick={() => router.push('/myTrainings')}>
              <ListItemText primary="My Training courses" />
            </ListItem>
          }
        </List>

        <Divider />

        {props.currentUser ?
          <List>
            <ListSubheader><Typography>{props.currentUser.fullName}</Typography></ListSubheader>

            <ListItem button key="0" onClick={() => router.push('/myProfile')}>
              <ListItemText primary="My Profile" />
            </ListItem>

            <ListItem button key="0" onClick={() => router.push('/resetPassword')}>
              <ListItemText primary="Reset password" />
            </ListItem>

            <ListItem button key="0" onClick={() => props.onSignout()}>
              <ListItemText primary="Sign Out" />
            </ListItem>
          </List> :

          <List>
            <ListItem button key="0" onClick={() => router.push('/signIn')}>
              <ListItemText primary="Sign In" />
            </ListItem>
          </List>
        }
      </div>
    </Drawer>
  )
}

function MainNavbar() {
  const classes = makeStyles((theme: Theme) => createStyles({
    root: {
      flexGrow: 1,
    },
    menuButton: {
      marginLeft: theme.spacing(2),
    },
    title: {
      flexGrow: 1,
      height: 54
    },
    appBar: {
      display: 'flex', 
      justifyContent: 'center', 
      flexDirection: 'row'
    },
    toolBar: {
      width: '100%',
      maxWidth: 1024
    }
  }))()

  const router = useRouter()
  const client = useApolloClient()
  const { currentUser } = useAuth()

  const [state, setState] = React.useState({ drawerOpen: false })
  const closeDrawer = () => (event: React.KeyboardEvent | React.MouseEvent) => {
    if (event.type === 'keydown' && ((event as React.KeyboardEvent).key === 'Tab' || (event as React.KeyboardEvent).key === 'Shift')) 
      return

    return setState({ ...state, drawerOpen: false })
  }

  return (
    <div className={classes.root}>
      <AppBar position="fixed" className={classes.appBar}>
        <Toolbar variant="dense" className={classes.toolBar}>
          <div className={classes.title}>
            <img src="/images/imit_logo.jpg" alt="" onClick={() => router.push('/')} />
          </div>

          <Hidden xsDown>
            <Button color="inherit" onClick={() => router.push('/')}>Catalog</Button>
          </Hidden>

          {currentUser ? 
            <Hidden xsDown>
              <Button color="inherit" onClick={() => router.push('/myTrainings')}>My Training courses</Button>
              <ProfileMenu
                currentUser={currentUser}
                onSignout={() => {
                  client.resetStore()
                  router.push('/')
                  removeCookie(authCookieName)
                }}
              />
            </Hidden> :

            <Hidden xsDown>
              <Button color="inherit" onClick={() => router.push('/signIn')}>Sign In</Button>
            </Hidden>
          }

          <Hidden smUp>
            <IconButton 
              edge="start" 
              className={classes.menuButton} 
              color="inherit" 
              aria-label="menu"
              onClick={() => setState({ drawerOpen: !state.drawerOpen })}
            >
              <MenuIcon />
            </IconButton>
          </Hidden>
        </Toolbar>
      </AppBar>

      <Hidden smUp>
        <MobileMenu 
          drawerOpen={state.drawerOpen} 
          closeDrawer={closeDrawer} 
          currentUser={currentUser} 
          onSignout={() => {
            client.resetStore()
            router.push('/')
            removeCookie(authCookieName)
          }}
        />
      </Hidden>
    </div>
  )
}

export default MainNavbar
