import * as React from 'react'
import {makeStyles, createStyles, Theme} from '@material-ui/core/styles'
import IProgram from '../../server/src/models/catalog/Program'
import { useRouter } from 'next/router'
import ProgramSummaryContent from './ProgramSummaryContent'

type Props = { program: IProgram, href?: any }

export default function ProgramSummary({ program, href }: Props) {
  const classes = makeStyles((theme: Theme) => createStyles({
    root: {
      marginBottom: 20,

      '&:hover': {
        //backgroundColor: '#F4F2F2',
        opacity: 0.8,
        cursor: 'pointer',
        borderTopRightRadius: 5,
        borderBottomRightRadius: 5
      }
    }
  }))()

  const router = useRouter()

  return (
    <div className={classes.root} onClick={() => href && router.push(href)}>
      <ProgramSummaryContent program={program} />
    </div>
  )
}

ProgramSummary.defaultProps = {
  href: null
} as Props
