import React, { useContext, createContext } from 'react'
import IAccountFields from '../server/src/models/accounts/Account'

const AuthContext = createContext<{ currentUser?: IAccountFields }>({ currentUser: undefined })

export const useAuth = () => useContext(AuthContext)

export function AuthProvider({ currentUser, children }: { currentUser?: IAccountFields, children: React.ReactNode }) {
  return (
    <AuthContext.Provider value={{ currentUser }}>
      {children}
    </AuthContext.Provider>
  )
}

export default AuthContext
