import { createMuiTheme } from '@material-ui/core/styles'

// Create a theme instance.
const theme = createMuiTheme({
  overrides: {
    MuiStepIcon: {
      root: {
        color: '#DFDFDF',
        fontWeight: 'bold',

       '&$completed': {
         color: '#76ad92',
        },
        '&$active': {
          color: '#76ad92',

        },
      },
      active: {},
      completed: {},
    }
  },

  typography: {
    button: {
      textTransform: 'none'
    }
  },

  palette: {
    primary: {
      main: '#FFFFFF'
    },

    secondary: {
      main: '#248857'
    },
    /*
    error: {
      main: red.A400
    },
    background: {
      default: '#fff'
    }
    */
  }
})

export default theme
