import React from 'react'
import IEnrollment from '../server/src/models/accounts/Enrollment'
import { Paper, Typography } from '@material-ui/core'

export default function EnrollmentItemContent({ enrollment }: { enrollment: IEnrollment }) {
  const statusColor = () => {
    if (enrollment.enrollmentStatus === 'Pending') return 'orange'
    if (enrollment.enrollmentStatus === 'Rejected') return 'red'
    if (enrollment.enrollmentStatus === 'Approved') return 'green'
    return 'gray'
  }

  return (
    <Paper style={{ padding: 10, display: 'flex', marginBottom: 10 }}>
      <div style={{ flexGrow: 1 }}>
        <Typography variant="h6" color="secondary">{enrollment.trainingTitle}</Typography>

        <div style={{ color: '#808080', fontSize: '9pt' }}>
          <strong>Program:</strong> {enrollment.programTitle}, &nbsp;
          <strong>Plan:</strong> {enrollment.planTitle}, &nbsp;
          <strong>Session:</strong> {enrollment.sessionTitle} ({enrollment.sessionStatus})
        </div>
      </div>

      <div style={{ color: '#808080', fontSize: '9pt' }}>
        <div><strong>Status:</strong></div>
        <Typography variant="body2" style={{ color: statusColor() }}>
          {enrollment.enrollmentStatus}
        </Typography>
      </div>
    </Paper>
  )
}
