import * as React from 'react'
import {makeStyles, createStyles, Theme} from '@material-ui/core/styles'
import { Typography, Grid } from '@material-ui/core'
import IDepartment from '../../server/src/models/catalog/Department'
import Link from 'next/link'
import ProgramSummary from './ProgramSummary'

type Props = { department: IDepartment }

export default function DepartmentBox({ department }: Props) {
  const classes = makeStyles((theme: Theme) => createStyles({
    root: {
      marginTop: 40
    },
    title: {
      backgroundColor: '#DFDFDF',
      color: '#808080',
      fontSize: '10pt',
      padding: 5,
      borderRadius: 3,
      fontWeight: 'bold',
      marginBottom: 15
    }
  }))()

  const { title, programs } = department

  return (
    <div className={classes.root}>
      <div className={classes.title}><Typography variant="inherit">{title}</Typography></div>

      <Grid container spacing={2}>
        {programs.map(p => 
          <Link key={p.id} href={{ pathname: '/program', query: { programId: p.id} }} passHref>
            <Grid item md={6}>
              <ProgramSummary program={p} />
            </Grid>
          </Link>
        )}
      </Grid>
    </div>
  )
}
